    private void FactorialbttnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FactorialbttnActionPerformed
        
        int factorial; 
        int fact = 1; 
        
        factorial = Integer.parseInt(Input1.getText());
        
        for(i=1;i<=factorial;i++)
        {    
            fact=fact*i;    
        }
        Output1.setText(String.valueOf(fact)); 
    }//GEN-LAST:event_FactorialbttnActionPerformed

