    private void PrimebttnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PrimebttnActionPerformed
        
        int prime; 
         
        prime = Integer.parseInt(Input2.getText());
        
        boolean flag = false;
        for(int i = 2; i <= prime/2; ++i)
        {
            // condition for nonprime number
            if(prime % i == 0)
            {
                flag = true;
                break;
            }
        }

        if (!flag)
            Output2.setText(String.valueOf(true));
        else
            Output2.setText(String.valueOf(false));
        
    }//GEN-LAST:event_PrimebttnActionPerformed

    private void FibonaccibttnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FibonaccibttnActionPerformed
        
        int fibonacci; 
        
        fibonacci = Integer.parseInt(Input3.getText());
        
        int n, a = 0, b = 0, c = 1;
        
        for(int i = 1; i <= fibonacci; i++)
        {
            a = b;
            b = c;
            c = a + b;
            Output3.setText(String.valueOf(a));
        }
    }//GEN-LAST:event_FibonaccibttnActionPerformed

